import React ,{useState,useEffect} from "react";
import CommonSection from "../components/Shared/CommonSection";
import "../style/tour.css";
import { Container, Row, Col } from "reactstrap";
import tours from "../assets/data/tours";
import Tourcard from "../components/Shared/Tourcard";
import Searchbar from "../components/Shared/Searchbar/Searchbar";
import Newsletter from "../components/Shared/Newsletter";




const Tours = () => {
  
  const [pageCount,setpageCount] = useState(0);
  const [page,setpage] = useState(0);

  useEffect(() => {
    const pages =Math.ceil(5/4);
    setpageCount(pages);
  }, [page])
  
  return (
    <>
    <Container>
    <section class="big-feature">
    <section class="big-feature-section container flex">
      <div class="big-feature flex">
        {/* <img src="./assets/asset 18.png" alt="" srcset=""/> */}
      </div>
      <div class="big-feature-desc">
        <h3>Effortless validation for</h3>
        <h1>Design Professional</h1>
        <p>Test interfaces, interaction flows, iconography and more, to help you create intuitive and delightful experiences for your users.</p>
        <br/>
        <a href="" class= "primary-button get-started-btn">Get Started</a>
      </div>
   </section>
   </section>
   </Container>

   <section class="big-feature-section container flex" style="flex-direction: row-reverse;" >
    <div class="big-feature flex">
      <img src="./assets/asset 24.png" alt="" srcset=""/>
    </div>
    <div class="big-feature-desc">
      <h3>Optimisation for</h3>
      <h1>Marketers</h1>
      <p>Fine-tune landing pages, messaging, and creative, helping you optimise conversion rates on marketing campaigns and product launches.</p>
      <br/>
      <a href="" class= "primary-button get-started-btn">Get Started</a>
    </div>

  </section>
      <CommonSection title={"All Tours"} />
      <section>
        <Container>
          <Row>
            <div className="search-bar">
            <Searchbar />
            </div>
          </Row>
        </Container>
      </section>

      <section className="pt-0">
        <Container>
          <Row>
            {tours.map(tour=><Col lg='3' className="mb-4" key={tour.id}>
             <Tourcard tour={tour}/></Col>)}
             <Col lg='12'>
                   <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map(number => (
                  <span key={number} onClick={()=>setpage(number)}
                  className={page===number?"active__page": "" }>
                    {number+1}
                  </span>
                ))}
                   </div>
             </Col>
          </Row>
        </Container>
      </section>
      <div className="nz">

      <Newsletter/>
      </div>
      
    </>
  );
};

export default Tours;
