    const contactRef= useRef(" ");

    const handleChange = (e) => {
        setbooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
      };

      const handleClick = async (e) => {

        e.preventDefault();
      };
      
  return (
    <>
    <Form className="booking_info__form" onSubmit={handleClick}>

<FormGroup>
  <input
    type="text"
    placeholder="Full Name"
    id="fullName"
    required
    onChange={handleChange}
    ref={nameRef} 
  />
</FormGroup>

<FormGroup>
  <input
    type="Phone"
    placeholder="Contact"
    id="contact"
    required
    onChange={handleChange}
    ref={contactRef}
    
  />
</FormGroup>

<FormGroup>
  <input
    type="number"
    placeholder="People"
    id="guestSize"
    required
    onChange={handleChange}
    ref={guestSizeRef} 
  />
</FormGroup>

<FormGroup className="d-flex align-items-center gap-3">
  <input
    type="date"
    placeholder=""
    id="date"
    required
    onChange={handleChange}
  />
