import React, { useState,useContext } from "react";
import "../style/login.css";
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

import loginIMg from "../assets/images/register.png";
import userIcon from "../assets/images/user.png";

import { AuthContext } from "../context/AuthContext";
import { BASE_URL } from "../Utilis/config";

const Register = () => {

  const [credential, setCredential] = useState({
    username: undefined,
    email: undefined,
    password: undefined,
  });

  const {dispatch} = useContext(AuthContext)
  const navigate = useNavigate()
   
  const handleClick = async (e) => {
    e.preventDefault();

    try {
     console.log(credential);
      const res = await fetch(`${BASE_URL}/auth/register`,{
        
        method:'post',
        headers:{
          'content-type':'application/json'
        },

        body:JSON.stringify(credential)
      })
  
      const result = await res.json()
      console.log(result);
     
      if(!res.ok) alert(result.message)

      dispatch({type:'REGISTER_SUCCESS'})
      alert("User Created Successfully");
       navigate('/login')
    }
    catch (err) {
      console.log("in catch");
      alert(err.message)
    }
  };

  const handleChange = (e) => {
    setCredential((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const verify=()=>{
    window.open ('https://www.signzy.com/fintech-apis/aadhaar-verification-api/')
  }

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login__container d-flex justify-content-between">
              <div className="login__img">
                <img src={loginIMg} alt="" />
              </div>

              <div className="login__form">
                <div className="user">
                  <img src={userIcon} alt="" />
                </div>
                <h2>Register</h2>

                <Form onSubmit={handleClick}>

                <FormGroup>
                    <input
                      type="username"
                      placeholder="Username"
                      required
                      id="username"
                      onChange={handleChange}

                    />
                  </FormGroup>

                  <FormGroup>
                    <input
                      type="email"
                      placeholder="Email"
                      required
                      id="email"
                      onChange={handleChange}
                    />
                  </FormGroup>

                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      required
                      id="password"
                      onChange={handleChange}
                    />
                  </FormGroup>

                  <Button  onClick={verify}  style={{fontSize:"25px", marginRight:"10px", backgroundColor: "#5F9EA0" , height:"50px" , borderRadius:"6px"}}
                    className="btn btn-secondary">
                    ID Verification
                  </Button>

                  <Button style={{fontSize:"25px",marginTop:"20px" , marginLeft:"45px",backgroundColor: "Green" /* Green */}}
                     className="btn btn-secondary"
                    type="submit"
                  >
                    Register
                  </Button>

                  <p  style={{fontSize:"17px",}}>
                  Already have an Account? <Link to="/login" style={{fontSize:"25px"}}>Login</Link>
                  </p>

                </Form>

              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Register;
