import React, { useState,useContext} from "react";
import './Create.css'
import { BASE_URL } from "../../Utilis/config";
import { AuthContext } from "../../context/AuthContext";

const Create = () => {

  const {user} = useContext(AuthContext);

  
  const [booking, setbooking] = useState({

    userId: user && user._id,
    userEmail:user && user.email,
    tourName : title,
    fullName: "",
    phone: "",
    guestSize: "",
    bookAt: "",
    date: "",
  });

  const handleCreate = async (e) => {

    e.preventDefault();
    // console.log(booking)

    try {
      if(!user|| user===undefined || user===null) {
        alert("Please Sign in")
      }
     
         const res  =await fetch(`${BASE_URL}/CreateTour`,{
          method:'post',
          headers:{
            'content-type':'application/json'
          },
          credentials:'include',
          body:JSON.stringify(CreateTour)
          
         })
         
         const result = await res.json();
         if(!res.ok)  {
         return alert(result.message)
         } 
         
    } catch (err) {
       alert(err.message)
    }
  
  };

  return (
   <>
   <div className='CreateFrom'>
   <form >
  <div className="form-row">

    <div className="form-group col-xs-2">
      <label for="inputEmail4">Title</label>
      <input type="text" className="form-control" id="Title" placeholder="Title"/>
    </div>

    <div className="form-group col-xs-2">
      <label for="inputPassword4">City</label>
      <input type="City" className="form-control" id="City" placeholder="City"/>
    </div>

  </div>

  <div className="form-group col-xs-2 ">
    <label for="inputAddress">Address</label>
    <input type="text" className="form-control" id="inputAddress" placeholder="Sydeny , Australia"/>
  </div>

  <div className="form-group ">
    <label for="inputAddress2">Distance</label>
    <input type="number" className="form-control" id="Distance" placeholder="Distance"/>
  </div>

  <div className="form-row col-xs-2">
    <div className="form-group col-xs-2">
      <label for="MaxGroup">MaxGroup</label>
      <input type="number" className="form-control" id="MaxGroup"/>
    </div>

    <div className="form-group col-md-6">
      <label for="Description">Description</label>
      <input type="text" className="form-control" id="Description"/>
    </div>

    <div className="form-group col-md-4 upload-form">
    <label for="image">Choose an image:</label>
        <input type="file" name="image" id="image" accept="image/*"/>
        <br/>
        <input type="submit" value="Upload"/>
    </div>

    <div className="form-group col-md-4">
      <label for="inputState">Add Image</label>
      <select id="Image" className="form-control">
        <option selected>Drop Here</option>
        <option>...</option>
      </select>
    </div>
  
  </div>

  <div className="form-group">
  </div>
  <button onClick={handleCreate} type="submit" className="btn btn-primary">Create</button>
</form>
</div>
<div className='mb-70'></div>
</>
  )
}

export default Create
