import React, { useState,useContext, useEffect ,useRef} from "react";
import "../style/login.css";
import { Container, Row, Col, Form, FormGroup, Button } from "reactstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

import loginIMg from "../assets/images/register.png";
import userIcon from "../assets/images/user.png";

import { AuthContext } from "../context/AuthContext";
import { BASE_URL } from "../Utilis/config";

const Register = () => {

  const [credential, setCredential] = useState({
    username: undefined,
    email: undefined,
    password: undefined,
  });

  const {dispatch} = useContext(AuthContext)
  const navigate = useNavigate()

  var [count, setcount] = useState(0);
   
  const handleClick = async (e) => {
    e.preventDefault();

    try {
     console.log(credential);
      const res = await fetch(`${BASE_URL}/auth/register`,{
        
        method:'post',
        headers:{
          'content-type':'application/json'
        },

        body:JSON.stringify(credential)
      })
  
      const result = await res.json()
      console.log(result);
     
      if(!res.ok) alert(result.message)

      dispatch({type:'REGISTER_SUCCESS'})
      alert("User Created Successfully");
       navigate('/login')
    }
    catch (err) {
      console.log("in catch");
      alert(err.message)
    }
  };

  const handleChange = (e) => {
    setCredential((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };
  const prevCountRef = useRef<number>(0);
  const verify=()=>{
    window.open ('https://www.signzy.com/fintech-apis/aadhaar-verification-api/');
    setcount=1;
    console.log(setcount);
  }

  useEffect(() => {
    /**
     * assign the latest render value of count to the ref
     * However, assigning a value to ref doesn't re-render the app
     * So, prevCountRef.current in the return statement displays the
     * last value in the ref at the time of render i.e., the previous state value.
     */
    prevCountRef.current = count;
  }, [count]); //run this code when the value of count changes
  

  return (
    <section>
      <Container>
        <Row>
          <Col lg="8" className="m-auto">
            <div className="login__container d-flex justify-content-between">
              <div className="login__img">
                <img src={loginIMg} alt="" />
              </div>

              <div className="login__form">
                <div className="user">
                  <img src={userIcon} alt="" />
                </div>
                <h2>Register</h2>

                <Form onSubmit={handleClick}>

                <FormGroup>
                    <input
                      type="username"
                      placeholder="Username"
                      required
                      id="username"
                      onChange={handleChange}

                    />
                  </FormGroup>

                  <FormGroup>
                    <input
                      type="email"
                      placeholder="Email"
                      required
                      id="email"
                      onChange={handleChange}
                    />
                  </FormGroup>

                  <FormGroup>
                    <input
                      type="password"
                      placeholder="Password"
                      required
                      id="password"
                      onChange={handleChange}
                    />
                  </FormGroup>

                  <Button  onClick={verify}  style={{fontSize:"20px", marginLeft:"25px",
                  backgroundColor: "#5F9EA0" , height:"50px" , borderRadius:"6px"}}
                  className="btn btn-secondary">
                  ID Verification
                  </Button>

                  <Button disabled={setcount===0} style={{fontSize:"25px",marginTop:"20px" , marginLeft:"40px",
                  backgroundColor: "Green" /* Green */}}
                  className="btn btn-secondary"
                  type="submit">
                  Register
                  </Button>

                  {/* <p  style={{fontSize:"17px",}}>
                  Already have an Account? <Link to="/login" style={{fontSize:"25px"}}>
                    <p style={{fontSize:"30px" , marginTop:"-20px" , border:"2px solid pink" , borderRadius:"5px" , boxSizing: "border-box"}}>Login</p></Link>
                    </p> */}
              
                </Form>

              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Register;
