import React from 'react'
import "../style/tourDetail.css";
import { Container, Row, Col,Form,ListGroup } from "reactstrap";

import { useParams } from 'react-router-dom';
import toursdata from '../assets/data/tours';
import CalculateavgRating from '../Utilis/avgrating';

const TourDetails = () => {

  const {id} = useParams();

  const tour  = toursdata.find(tour=>tour.id===id);

  const {photo,title,desc,price,reviews,city,distance,address,maxGroupSize} = tour ;

  const {totalRatintg,avgRating} = CalculateavgRating (reviews);

  return (
   <section>
    <Container>
    <Row>
      <Col lg='8'>
             <div className="tour__content">
              <img src={photo} alt="" />
              <div className="tour__info">
                <h2>{title}</h2>
                <div className="d-flex align-items-center gap-5">
                <span className="tour__rating d-flex align-items-center gap-1">
                <i class="ri-star-fill" style={{color : "var(--secondary-color)" }}></i> {CalculateavgRating===0?null:avgRating}
                {totalRatintg===0 ? (
                    "Not Rated"
                ) : (<span>({reviews.length})</span>
                )}
                </span>

                <span><i class="ri-map-pin-line"></i> {address}</span>
                </div>

                <div className="tour-extra-details">
                  <span><i class="ri-map-pin-add-line"></i> {city}</span>
                  <span><i class="ri-exchange-dollar-line"></i>${price}/Person</span>
                  <span><i class="ri-group-line"></i> {maxGroupSize}</span>
                </div>

                <h5>Description</h5>
                <p>{desc}</p>
              </div>

              
             </div>
      </Col>
    </Row>
    </Container>
   </section>
  )
}

export default TourDetails
